mapper.select:
select * from emp where ( quit_yn = 'N' and dept_code = #{deptCode} and job_code in
#{code}
) or quit_date >= (TO_TIMESTAMP_TZ(TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'Asia/Seoul') order by job_code

select count(*) from emp e join dept d on e.dept_code = d.dept_code join job j on e.job_code = j.job_code where ( quit_yn = 'N'
and d.dept_title like '%' || #{searchKeyword} ||'%'
and j.job_title like '%' || #{searchKeyword} ||'%'
and e.name like '%' || #{searchKeyword} ||'%'
and e.dept_code = #{deptCode}
) or quit_date >= (TO_TIMESTAMP_TZ(TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'Asia/Seoul')

select e.*, j.job_title, d.dept_title, (select rename_filename from attachment where pk_no = e.emp_id) renameFilename from emp e join dept d on e.dept_code = d.dept_code join job j on e.job_code = j.job_code where ( quit_yn = 'N'
and d.dept_title like '%' || #{searchKeyword} ||'%'
and j.job_title like '%' || #{searchKeyword} ||'%'
and e.name like '%' || #{searchKeyword} ||'%'
and e.dept_code = #{deptCode}
) or quit_date >= (TO_TIMESTAMP_TZ(TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'Asia/Seoul') order by e.dept_code,e.job_code
