mapper.select:
select * from view_sign where status_emp_id = #{empId} and status in
#{status}
order by reg_date desc

mapper.update:
update
	signStatus
set
	status = #{status},
	reg_date = TO_TIMESTAMP_TZ(TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'Asia/Seoul',
	reason = #{reason}
where
	emp_id = #{empId}
	and
	sign_no = #{signNo}
